import { useEffect, useState } from "react";

import Axios from 'axios';

export const workouts = [
    {
        "id": "abc",
        "title": "push",
        "date": "08/08/2023",
        "exercises": [
            {
                "name": "bench press",
                "sets": [
                    [9, 185],
                    [9, 185],
                    [8, 185],
                    [7, 185]
                ]
            },
            {
                "name": "incline dumbell press",
                "sets": [
                    [5, 80],
                    [5, 80],
                    [4, 80],
                    [5, 80]
                ]
            },
            {
                "name": "cable lateral raise",
                "sets": [
                    [8, 12.5],
                    [8, 12.5],
                    [7, 12.5],
                    [7, 12.5]
                ]
            }
        ]
    },
    {   
        "id": "xyz",
        "title": "",
        "date": "08/08/2023",
        "exercises": [
            {
                "name": "bench press",
                "sets": [
                    [185, 9],
                    [185, 9],
                    [185, 8],
                    [185, 7]
                ]
            },
            {
                "name": "incline dumbell press",
                "sets": [
                    [80, 5],
                    [80, 5],
                    [80, 4],
                    [80, 5]
                ]
            },
            {
                "name": "cable lateral raise",
                "sets": [
                    [12.5, 8],
                    [12.5, 8],
                    [12.5, 7],
                    [12.5, 7]
                ]
            }
        ]
    },
];

const Home = () => {
    const [viewMode, setViewMode] = useState({"abc": false, "xyz": false});
    const [allWorkouts, setAllWorkouts] = useState([]);

    useEffect(() => {
        Axios.get('http://localhost:3001/api/get').then((response) => {
            setAllWorkouts([]);
        });
    }, []);

    return (
        <div className="workout-list">
          <a href="/edit/x" className="new-workout-link">
            New Workout
          </a>
          <div className="workouts-container">
            {workouts.map((workout, index) => (
              <div key={index} className="workout-item">
                <span className="workout-date">{workout.date}</span>
                {workout.title && <span className="workout-title">{workout.title}</span>}
                <a href={`/edit/${workout.id}`} className="edit-link">
                  Edit
                </a>
                <button
                  onClick={() => {
                    const newViewMode = { ...viewMode };
                    newViewMode[workout.id] = true;
                    setViewMode(newViewMode);
                  }}
                  className="view-button"
                >
                  View
                </button>
                {viewMode[workout.id] && (
                  <dialog open className="view-dialog">
                    <h2>{workout.date}</h2>
                    {workout.title && <h2>{workout.title}</h2>}
                    <div className="exercise-list">
                      {workout.exercises.map((exercise, index) => (
                        <div key={index} className="exercise-item">
                          <div className="exercise-name">{exercise.name}</div>
                          <div className="set-list">
                            {exercise.sets.map((set, ind) => (
                              <div key={ind} className="set-item">
                                <span>{`${set[1]} reps, ${set[0]}lbs`}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                      ))}
                    </div>
                    <button
                      onClick={() => {
                        const newViewMode = { ...viewMode };
                        newViewMode[workout.id] = false;
                        setViewMode(newViewMode);
                      }}
                      className="close-button"
                    >
                      Close
                    </button>
                  </dialog>
                )}
              </div>
            ))}
          </div>
        </div>
      );
}

export default Home;
