import { useRef, useState } from "react";

const workouts = [
    {
        "id": "abc",
        "title": "push",
        "date": "08/08/2023",
        "exercises": [
            {
                "name": "bench press",
                "sets": [
                    [185, 9],
                    [185, 9],
                    [185, 8],
                    [185, 7]
                ]
            },
            {
                "name": "incline dumbell press",
                "sets": [
                    [80, 5],
                    [80, 5],
                    [80, 4],
                    [80, 5]
                ]
            },
            {
                "name": "cable lateral raise",
                "sets": [
                    [12.5, 8],
                    [12.5, 8],
                    [12.5, 7],
                    [12.5, 7]
                ]
            }
        ]
    },
    {   
        "id": "xyz",
        "title": "",
        "date": "08/08/2023",
        "exercises": [
            {
                "name": "bench press",
                "sets": [
                    [185, 9],
                    [185, 9],
                    [185, 8],
                    [185, 7]
                ]
            },
            {
                "name": "incline dumbell press",
                "sets": [
                    [80, 5],
                    [80, 5],
                    [80, 4],
                    [80, 5]
                ]
            },
            {
                "name": "cable lateral raise",
                "sets": [
                    [12.5, 8],
                    [12.5, 8],
                    [12.5, 7],
                    [12.5, 7]
                ]
            }
        ]
    },
];

const Home = () => {
    const [viewMode, setViewMode] = useState({"abc": false, "xyz": false});

    return (
        <div>
            <a href="/edit/x">New Workout</a>
            <div>
                {workouts.map((workout, index) => {
                    return (
                    <div key={index}>
                        <span>{workout.date}</span>
                        {workout.title && <span>{workout.title}</span>}
                        <a href={`/edit/${workout.id}`}>Edit</a>
                        <button onClick={() => {
                            const newViewMode = {...viewMode};
                            newViewMode[workout.id] = true;
                            console.log(newViewMode);
                            setViewMode(newViewMode);
                        }}>View</button>
                        {viewMode[workout.id] && <dialog open>
                            <h2>{workout.date}</h2>
                            {workout.title && <h2>{workout.title}</h2>}
                            <div>
                                {workout.exercises.map((exercise, index) => {
                                    return (
                                    <div key={index}>
                                        <div>{exercise.name}</div>
                                        <div>
                                            {exercise.sets.map((set, ind) => {
                                                return (
                                                <div key="ind">
                                                    <span>{`${set[1]} reps, ${set[0]}lbs`}</span>
                                                </div>
                                                );
                                            })}
                                        </div>
                                    </div>
                                    );
                                })}
                            </div>
                            <button onClick={() => {
                                const newViewMode = {...viewMode};
                                newViewMode[workout.id] = false;
                                setViewMode(newViewMode);
                            }}>Close</button>
                        </dialog>}
                    </div>
                    );
                })}
            </div>
        </div>
    );
}

export default Home;
