import { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { useLocation } from 'react-router-dom';

const Edit = () => {
    const alertify = require('alertifyjs');
    const [data, setData] = useState([]);
    const [selectedDate, setSelectedDate] = useState(null);
    const [name, setName] = useState('');
    const location = useLocation();

    console.log(location.pathname.split('/')[1]);

    const handleDateChange = (e) => {
        setSelectedDate(e);
    };

    const handleNameChange = (e) => {
        setName(e);
    };

    const handleSubmit = () => {
        console.log("submitted");
    };

    return (
        <div>
            <label>Title (optional):</label>
            <input type="text" value={name} onChange={handleNameChange}></input>
            <label>Date:</label>
            <DatePicker selected={selectedDate} onChange={handleDateChange} required={true}/>
            <button onClick={
                () => {
                    const newExercise = {
                        "name": "template name",
                        "sets": []
                    };
                    setData((data) => [...data, newExercise]);
                }
            }>Add Exercise</button>
            <div>
            {data.map((exercise, index) => {
                return (
                <div key={index}>
                    <div>{exercise.name}</div>
                    <div>
                        {exercise.sets.map((set, ind) => {
                            return (
                                <div key={ind}>
                                    <span>
                                        <label>Reps:</label>
                                        <input type="number" value={set[0]} onChange={(e) => {
                                            const newData = [...data];
                                            newData[index].sets[ind][0] = e.target.value;
                                            setData(newData);
                                        }}/>
                                    </span>
                                    <span>
                                        <label>Weight:</label>
                                        <input type="text" value={set[1]} onChange={(e) => {
                                            const newData = [...data];
                                            newData[index].sets[ind][1] = e.target.value;
                                            setData(newData);
                                        }}/>
                                    </span>
                                </div>
                            );
                        })}
                    </div>
                    <button onClick={() => {
                        const newData = [...data];
                        newData[index].sets.push([0, "0"]);
                        setData(newData);
                    }}>Add Set</button>
                </div>
                );
            })}
            </div>
            <a href="/">Cancel</a>
            <form onSubmit={handleSubmit}>
                <button type="Submit">OK</button>
            </form>
        </div>
    );
}

export default Edit;
