import { useState } from "react";

const workouts = [
    {
        "id": "abc",
        "title": "push",
        "date": "08/08/2023",
        "exercises": [
            {
                "name": "bench press",
                "sets": [
                    [185, 9],
                    [185, 9],
                    [185, 8],
                    [185, 7]
                ]
            },
            {
                "name": "incline dumbell press",
                "sets": [
                    [80, 5],
                    [80, 5],
                    [80, 4],
                    [80, 5]
                ]
            },
            {
                "name": "cable lateral raise",
                "sets": [
                    [12.5, 8],
                    [12.5, 8],
                    [12.5, 7],
                    [12.5, 7]
                ]
            }
        ]
    },
    {   
        "id": "xyz",
        "title": "",
        "date": "08/08/2023",
        "exercises": [
            {
                "name": "bench press",
                "sets": [
                    [185, 9],
                    [185, 9],
                    [185, 8],
                    [185, 7]
                ]
            },
            {
                "name": "incline dumbell press",
                "sets": [
                    [80, 5],
                    [80, 5],
                    [80, 4],
                    [80, 5]
                ]
            },
            {
                "name": "cable lateral raise",
                "sets": [
                    [12.5, 8],
                    [12.5, 8],
                    [12.5, 7],
                    [12.5, 7]
                ]
            }
        ]
    },
];

/*
for each workout have an edit and view button
view expands the workout so that you see all of the exercises
once you are in view mode the view button becomes exit and the exit button returns you to initial state
*/

const Home = () => {
    const [viewMode, setViewMode] = useState({"abc": false, "xyz": false});
    return (
        <div>
            <a href="/edit/x">New Workout</a>
            <div>
                {workouts.map((workout, index) => {
                    return (
                    <div key={index}>
                        <span>{workout.date}</span>
                        {workout.title && <span>{workout.title}</span>}
                        <a href={`/edit/${workout.id}`}>Edit</a>
                        <button onClick={() => {
                            const newViewMode = {...viewMode};
                            newViewMode[workout.id] = true;
                            console.log(newViewMode);
                            setViewMode(newViewMode);
                        }}>View</button>
                        <div className={`dialog ${viewMode[workout.id] ? "open" : "closed"}`}>
                            <h2>{workout.date}</h2>
                            {workout.title && <h2>{workout.title}</h2>}
                            <button onClick={() => {
                                const newViewMode = {...viewMode};
                                newViewMode[workout.id] = false;
                                setViewMode(newViewMode);
                            }}>Close</button>
                        </div>
                    </div>
                    );
                })}
            </div>
        </div>
    );
}

export default Home;
